import React, { useEffect, useState } from 'react';
import jwt_decode from "jwt-decode"
import { useNavigate } from 'react-router-dom';
import { getTodo, postTodo } from '../../apis/api/todo';
import { useQuery } from 'react-query';

function Main(props) {
    const navigate =  useNavigate();
    const [ todoInput, setTodoInput ] = useState("");
    
    const todoList = useQuery(["todoList"], async () => {
        try {
            const option = {
                headers: {
                    Authorization: localStorage.getItem("accessToken")
                }
            }
            const response = await getTodo(option);
            console.log(response);
            console.log(todoList)
        } catch (error) {
            console.log(error)
        }
    });


    useEffect(()=>{
        try {
            let decoded = jwt_decode(localStorage.getItem("accessToken").substring(7))
        } catch (error) {
            console.log(error)
        }
        if(!localStorage.getItem("accessToken")) {
            navigate("/auth/signin")
        }
    }, [])

    const handleOnChange = (e) => {
        setTodoInput(e.target.value);
        console.log(todoInput)
    }


    const handleAddTodo = async () => {
        console.log('클릭')
        


        const option = {
            headers: {
                Authorization: localStorage.getItem("accessToken")
            }
        }
        try {
            const response = await postTodo({
                content: todoInput
            }, option);
            console.log(response.data)
            // window.location.reload();
        } catch (error) {
            console.log(error.response.data)
        }   
    }

    // useEffect(()=>{
    //     console.log(todoItem)
    // },
    // [todoItem])

    useEffect(()=>{

    },[todoList])

    

    return (
        <div>
            <div>
                <input placeholder='todolist' onChange={handleOnChange}/>
                <button onClick={handleAddTodo}>todo 추가</button>
            </div>
            <ul>
                {todoList.isLoading ? "" : todoList?.data?.data}
                {/* todoList의 로딩이 끝나고 todoList값이 있을 때 가져온다 */}
            </ul>
        </div>
    );
}

export default Main;
