import axios from 'axios';
import React, { useState } from 'react';
import { signup } from '../../apis/api/account';



function Signup(props) {
    const emptyAccount = {
        email : "",
        password : "",
        name : ""
    }
    const [ account, setAccount ] = useState(emptyAccount);


    const handleOnChange = (name, value) => {
        setAccount({
            account,
            [name]: value
        })
        console.log(account)
    }

    const handleSignupSubmit = () => {
        try {
            const response = signup(account)
        } catch (error) {
            const responseErrorMessage = error.response.data;
            console.log(responseErrorMessage);
        }
    }

    return (
        <div>
            <h1>회원가입</h1>
            <input placeholder='email' type='text' onChange={handleOnChange}/>
            <input placeholder='password' type='password' onChange={handleOnChange}/>
            <input placeholder='name' type='text' onChange={handleOnChange}/>
            <button onClick={handleSignupSubmit}>가입</button>
        </div>
    );
}

export default Signup;
