import React, { useEffect, useState } from 'react';
import jwt_decode from "jwt-decode"
import { useNavigate } from 'react-router-dom';
import { postTodo } from '../../apis/api/todo';

function Main(props) {
    const navigate =  useNavigate();
    const [ todoInput, setTodoInput ] = useState("");
    const defaultTodo = {
        token: "",
        item: ""
    }
    const [ todoItem, setTodoItem ] = useState()
    const [ todoList, setTodoList ] = useState([])
    

    useEffect(()=>{
        try {
            let decoded = jwt_decode(localStorage.getItem("accessToken").substring(7))
        } catch (error) {
            console.log(error)
        }
        if(!localStorage.getItem("accessToken")) {
            navigate("/signin")
        }
    }, [])

    const handleOnChange = (e) => {
        setTodoInput(e.target.value);
    }


    const handleOnClick = () => {
        console.log('클릭')
        setTodoItem({
            userEmail: jwt_decode(localStorage.getItem("accessToken").substring(7)).username,
            item: todoInput
        })
    }
    useEffect(async()=>{
        setTodoList(todoList.concat(todoItem));
        try {
            const response = await postTodo(todoItem);
            console.log(response.data)
            window.location.reload();
        } catch (error) {
            console.log(error.response.data)
        }
        console.log(todoItem)
    },
    [todoItem])

    useEffect(()=>{

    },[todoList])

    return (
        <div>
            <input placeholder='todolist' onChange={handleOnChange}/>
            <button onClick={handleOnClick}>todo 추가</button>
            <div>
                {todoList.map(todo => {
                    <span>{todo}</span>
                })}
            </div>
        </div>
    );
}

export default Main;
