import React, { useEffect, useState } from 'react';
import jwt_decode from "jwt-decode"
import { useNavigate } from 'react-router-dom';
import { deleteTodo, getTodo, postTodo, updateTodo } from '../../apis/api/todo';
import { useQuery } from 'react-query';

function Main(props) {
    const navigate =  useNavigate();
    const [ todoInput, setTodoInput ] = useState("");
    const option = {
        headers: {
            Authorization: localStorage.getItem("accessToken")
        }
    }

    const todoList = useQuery(["todoList"], async () => {
        try {

            const response = await getTodo(option);
            return response;
        } catch (error) {
            console.log(error)
        }
    });


    useEffect(()=>{
        try {
            let decoded = jwt_decode(localStorage.getItem("accessToken").substring(7))
        } catch (error) {
            console.log(error)
        }
        if(!localStorage.getItem("accessToken")) {
            navigate("/auth/signin")
        }
    }, [])

    const handleOnChange = (e) => {
        setTodoInput(e.target.value);
    }


    const handleUpdateTodo = async () => {
        const response = await updateTodo(option);
    }

    const handleDeleteTodo = async () => {
        const response = await deleteTodo(option);
    }

    const handleAddTodo = async () => {
        console.log('클릭')
        try {
            const response = await postTodo({
                content: todoInput
            }, option);
            todoList.refetch();
        } catch (error) {
            console.log(error.response.data)
        }   
    }

    // useEffect(()=>{
    //     console.log(todoItem)
    // },
    // [todoItem])

    useEffect(()=>{

    },[todoList])

    console.log(todoList)
    
    return (
        <div>
            <div>
                <input placeholder='todolist' onChange={handleOnChange}/>
                <button onClick={handleAddTodo}>todo 추가</button>
            </div>
                <ul>
                    {todoList.isLoading ? "" : todoList?.data?.data.map(todo => 
                        <li key={todo.todoId} >{todo.todo}</li>
                    )}
                    {/* todoList의 로딩이 끝나고 todoList값이 있을 때 가져온다 */}
                </ul>
        </div>
    );
}

export default Main;
