import React, { useEffect, useState } from 'react';
import jwt_decode from "jwt-decode"
import { useNavigate } from 'react-router-dom';
import { postTodo } from '../../apis/api/todo';

function Main(props) {
    const navigate =  useNavigate();
    const [ todoInput, setTodoInput ] = useState("");
    const defaultTodo = {
        content: ""
    }
    const [ todoItem, setTodoItem ] = useState()
    const [ todoList, setTodoList ] = useState([])
    

    useEffect(()=>{
        try {
            let decoded = jwt_decode(localStorage.getItem("accessToken").substring(7))
        } catch (error) {
            console.log(error)
        }
        if(!localStorage.getItem("accessToken")) {
            navigate("/auth/signin")
        }
    }, [])

    const handleOnChange = (e) => {
        setTodoInput(e.target.value);
        console.log(todoInput)
    }


    const handleAddTodo = async () => {
        console.log('클릭')
        
        setTodoItem({
            content: todoInput
        })
        const option ={
        Authorization: localStorage.getItem("accessToken")
        }

        console.log(todoItem)
        console.log(option.Authorization)

        try {
            const response = await postTodo(todoItem, option);
            console.log(response.data)
            // window.location.reload();
        } catch (error) {
            console.log(error.response.data)
        }   
    }

    // useEffect(()=>{
    //     console.log(todoItem)
    // },
    // [todoItem])

    useEffect(()=>{

    },[todoList])

    return (
        <div>
            <div>
                <input placeholder='todolist' onChange={handleOnChange}/>
                <button onClick={handleAddTodo}>todo 추가</button>
            </div>
            <ul>
                {todoList.map(todo => {
                    <li>{todo}</li>
                })}
            </ul>
        </div>
    );
}

export default Main;
