    // if(authenticateState.isError) {
    //     for(let path of permitAllPath) {
    //         if(pathname.startsWith(path)) {
    //             return element;
    //         }
    //     }
    //     return <Navigate to={"/"}/>
    // }

    // for(let path of permitAllPath) {
    //     if(pathname.startsWith(path)) {
    //         return <Navigate to={"/"}/>
    //     }
    // }

    
    return elementState;
}

export default AuthRoute;
